package main

import (
	"leetcode/algorithms/0567.PermutationinString/checkInclusion"
	"testing"
)

func TestCheckInclusion(t *testing.T) {
	tests := []struct {
		s1     string
		s2     string
		output bool
	}{
		{"adc", "dcda", true},
		{"ab", "eidbaooo", true},
		{"ab", "eidboaoo", false},
		{"hello", "ooolleoooleh", false},
		{"vxqakfyaqahufxfizupjrkkifjlbfqfeprqrfjvxnubntdtlvz",
			"",
			false},
	}

	for _, test := range tests {
		ret := checkInclusion.CheckInclusion(test.s1, test.s2)
		if ret != test.output {
			t.Errorf("Got %t for s1 = %s, s2 = %s; expected %t", ret, test.s1, test.s2, test.output)
		}
	}
}
