package main

import (
	"leetcode/algorithms/1071.GreatestCommonDivisorofStrings/gcdOfStrings"
	"testing"
)

func TestGcdOfStrings(t *testing.T)  {
	tests := []struct{
		str1 string
		str2 string
		output string
	}{
		{"ABCABC", "ABC", "ABC"},
		{"ABABAB",  "ABAB", "AB"},
		{"LEET", "CODE",""},
		{"TAUXXTAUXXTAUXXTAUXXTAUXX","TAUXXTAUXXTAUXXTAUXXTAUXXTAUXXTAUXXTAUXXTAUXX","TAUXX"},
		{"",
			"",
		"AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA"},
	}

	for _, test := range tests {
		ret := gcdOfStrings.GcdOfStrings(test.str1, test.str2)
		if ret != test.output {
			t.Errorf("Got %s for input %s, %s; expected %s", ret, test.str1, test.str2, test.output)
		}
	}
}